    <script>
      $(document).ready(function() {
          $('#subscribe-form').on('submit', function(event) {
              event.preventDefault(); // Prevent the default form submission
  
              var formData = $(this).serialize(); // Serialize form data
  
              $.ajax({
                  url: $(this).attr('action'), // Get form action URL
                  type: 'POST',
                  data: formData,
                  success: function(response) {
                      // Display flash message
                      Toastify({
                      text: "You subscrbe our news letters",
                      duration: 3000,
                      close: true,
                      gravity: "top", // `top` or `bottom`
                      position: "right", // `left`, `center` or `right`
                      stopOnFocus: true, // Prevents dismissing of toast on hover
                      style: {
                          background: "#28a745", // Green background for success
                      },
                  }).showToast();
                      $('#subscribe-form')[0].reset(); // Reset form after successful submission
                  },
                  error: function(xhr) {
                      var errors = xhr.responseJSON.errors;
                      var errorHtml = '<ul>';
                      $.each(errors, function(key, value) {
                          errorHtml += '<li>' + value[0] + '</li>';
                      });
                      errorHtml += '</ul>';
                      $('#message').html('<p style="color: red;">' + errorHtml + '</p>');
                  }
              });
          });
      });
  </script>
        
